                    <Button 
                      type="submit"
                      disabled={telegramMutation.isPending}
                      data-testid="button-save-telegram"
                    >
                      {telegramMutation.isPending ? "Saving..." : "Save Settings"}
                    </Button>
                  </div>
                </form>
              </CardContent>
            </Card>
          </div>

          {/* Appearance Settings */}
