                  <Palette className="w-5 h-5 mr-2" />
                  Choose Theme
                </h3>

                <div className="space-y-3">
                  <div 
                    className={`flex items-center space-x-3 p-3 rounded-lg cursor-pointer transition-all ${
                      theme === "dark" ? "bg-primary/10 border border-primary/20" : "bg-muted/20"
                    }`}
