                  <div className="h-2 bg-muted rounded-full w-1/2 mb-2"></div>
                  <div className="h-2 bg-muted rounded-full w-2/3"></div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

