  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'app_clone': return <Smartphone className="w-4 h-4" />;
      case 'fake_wifi': return <Wifi className="w-4 h-4" />;
      case 'qr_code': return <Camera className="w-4 h-4" />;
