      default: return <Smartphone className="w-4 h-4" />;
    }
  };

  const getPlatformIcon = (platform: string) => {
    switch (platform) {
      case 'android': return 'ðŸ¤–';
      case 'ios': return 'ðŸŽ';
      case 'both': return 'ðŸ“±';
