import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { 
  User, 
  Plus, 
  Target,
  MapPin,
  Building,
  Clock,
  Mail,
  Edit,
  Trash2,
  Globe,
  Users,
  Calendar
} from "lucide-react";

const personalizationRuleSchema = z.object({
  name: z.string().min(3, "Rule name must be at least 3 characters"),
  description: z.string().min(10, "Description must be at least 10 characters"),
  targetAudience: z.object({
    department: z.string().optional(),
    location: z.string().optional(),
    role: z.string().optional(),
    seniority: z.string().optional(),
    industry: z.string().optional(),
  }),
  personalization: z.object({
    subjectLine: z.string().optional(),
    senderName: z.string().optional(),
    senderEmail: z.string().optional(),
    companyName: z.string().optional(),
    urgencyLevel: z.enum(["low", "medium", "high"]).optional(),
    timeZone: z.string().optional(),
  }),
  template: z.object({
    templateId: z.string(),
    customizations: z.record(z.string()),
  }),
  isActive: z.boolean().default(true),
  priority: z.number().min(1).max(10).default(5),
});

type PersonalizationRuleData = z.infer<typeof personalizationRuleSchema>;

interface PersonalizationRule {
  id: string;
  name: string;
  description: string;
  targetAudience: {
    department?: string;
    location?: string;
    role?: string;
    seniority?: string;
    industry?: string;
  };
  personalization: {
    subjectLine?: string;
    senderName?: string;
    senderEmail?: string;
    companyName?: string;
    urgencyLevel?: string;
    timeZone?: string;
  };
  template: {
    templateId: string;
    customizations: Record<string, string>;
  };
  isActive: boolean;
  priority: number;
  createdAt: string;
  lastUsed?: string;
  usage: {
    campaigns: number;
    recipients: number;
    effectiveness: number;
  };
}

const mockRules: PersonalizationRule[] = [
  {
    id: "1",
    name: "UK C-Suite Banking",
    description: "High-urgency banking alerts targeting senior executives in UK financial sector",
    targetAudience: {
