import { SmtpServer } from '@shared/schema';
import { storage } from './storage';
import { emailService } from './emailService';
import { smsService } from './smsService';
import { webhookService } from './webhookService';

export interface SmtpTestResult {
  success: boolean;
  error?: string;
  details: {
    connectionTime: number;
    authenticationTime: number;
    sendTime?: number;
  };
}

export interface SmsTestResult {
  success: boolean;
  messageId?: string;
  error?: string;
  deliveryTime: number;
}

export interface WebhookTestResult {
  success: boolean;
  responseCode?: number;
  responseTime: number;
  error?: string;
}

export class ConfigurationService {

  // SMTP Configuration and Testing
  async testSmtpConfiguration(smtpConfig: Omit<SmtpServer, 'id' | 'userId' | 'createdAt'>): Promise<SmtpTestResult> {
    const startTime = Date.now();

    try {
      console.log(`Testing SMTP configuration for ${smtpConfig.host}:${smtpConfig.port}`);

      // Connection test
      const connectionStart = Date.now();
      const isConnected = await emailService.testSmtpConnection(smtpConfig);
      const connectionTime = Date.now() - connectionStart;

      if (!isConnected) {
        return {
          success: false,
          error: 'Failed to connect to SMTP server',
          details: { connectionTime, authenticationTime: 0 }
        };
      }

      // Authentication test
      const authStart = Date.now();
      const isAuthenticated = await emailService.testSmtpAuthentication(smtpConfig);
      const authenticationTime = Date.now() - authStart;

      if (!isAuthenticated) {
        return {
          success: false,
          error: 'SMTP authentication failed',
          details: { connectionTime, authenticationTime }
        };
      }

      // Send test email - use existing sendTestEmail method
      const sendStart = Date.now();
      const testResult = await emailService.sendTestEmail(smtpConfig as any, smtpConfig.fromEmail);
      const sendTime = Date.now() - sendStart;

      return {
        success: testResult.success,
        error: testResult.error,
        details: { connectionTime, authenticationTime, sendTime }
      };

    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred',
        details: { connectionTime: Date.now() - startTime, authenticationTime: 0 }
      };
    }
  }

  async saveSmtpConfiguration(userId: string, smtpConfig: Omit<SmtpServer, 'id' | 'userId' | 'createdAt'>): Promise<SmtpServer> {
    // Test configuration before saving
    const testResult = await this.testSmtpConfiguration(smtpConfig);

    if (!testResult.success) {
      throw new Error(`SMTP configuration test failed: ${testResult.error}`);
    }

    // Deactivate all other SMTP servers for this user if this one is being set as active
    if (smtpConfig.isActive) {
      const userSmtpServers = await storage.getSmtpServersByUserId(userId);
      for (const server of userSmtpServers) {
        if (server.isActive) {
          await storage.updateSmtpServer(server.id!, { ...server, isActive: false });
        }
      }
    }

    return await storage.createSmtpServer({
      ...smtpConfig,
      userId
    });
  }

  // SMS Configuration and Testing
  async testSmsConfiguration(provider: string, apiKey: string, apiSecret: string, fromNumber: string, testNumber: string): Promise<SmsTestResult> {
    const startTime = Date.now();

    try {
      const testResult = await smsService.sendTestSms({
        name: provider,
        apiKey,
        apiSecret,
        fromNumber
      }, testNumber);

      return {
        success: testResult.success,
        messageId: testResult.messageId,
        error: testResult.error,
        deliveryTime: Date.now() - startTime
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred',
        deliveryTime: Date.now() - startTime
      };
    }
  }

  // Webhook Configuration and Testing
  async testWebhookConfiguration(url: string, secret?: string): Promise<WebhookTestResult> {
    const startTime = Date.now();

    try {
      const endpoint = {
        url,
        secret,
        events: ['test.webhook'],
        isActive: true
      };

      const success = await webhookService.testWebhook(endpoint);

      return {
        success,
        responseCode: success ? 200 : 500,
        responseTime: Date.now() - startTime,
        error: success ? undefined : 'Webhook test failed'
      };
    } catch (error) {
      return {
        success: false,
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  // Environment Configuration
  async getEnvironmentConfiguration(): Promise<{
    environment: string;
    database: boolean;
    smtp: boolean;
    sms: boolean;
    webhooks: boolean;
    features: string[];
  }> {
    const environment = process.env.NODE_ENV || 'development';

    // Check service availability
    const database = await this.checkDatabaseConnection();
    const smtp = await this.checkSmtpAvailability();
    const sms = await this.checkSmsAvailability();
    const webhooks = await this.checkWebhookAvailability();

    const features = [
      'email_campaigns',
      'sms_campaigns',
      'webhook_integrations',
      'real_time_analytics',
      'ab_testing',
      'scheduled_campaigns',
      'risk_scoring',
      'compliance_reporting'
    ];

    return {
      environment,
      database,
      smtp,
      sms,
      webhooks,
      features
    };
  }

  private async checkDatabaseConnection(): Promise<boolean> {
    try {
      // Try to perform a simple database operation
      await storage.getUserByUsername('test-connection-check');
      return true;
    } catch (error) {
      return false;
    }
  }

  private async checkSmtpAvailability(): Promise<boolean> {
    // Check if any SMTP servers are configured
    try {
      const smtpServers = await storage.getSmtpServers();
      return smtpServers.length > 0;
    } catch (error) {
      return false;
    }
  }

  private async checkSmsAvailability(): Promise<boolean> {
    // Check if SMS environment variables are set
    return !!(process.env.TWILIO_ACCOUNT_SID || process.env.AWS_ACCESS_KEY_ID);
  }

  private async checkWebhookAvailability(): Promise<boolean> {
    // Webhooks are always available as they don't require external dependencies
    return true;
  }

  // Security Configuration
  async getSecurityConfiguration(): Promise<{
    rateLimit: {
      enabled: boolean;
      windowMs: number;
      maxRequests: number;
    };
    authentication: {
      tokenExpiration: number;
      refreshExpiration: number;
      passwordPolicy: {
        minLength: number;
        requireUppercase: boolean;
        requireLowercase: boolean;
        requireNumbers: boolean;
        requireSpecialChars: boolean;
      };
    };
    encryption: {
      algorithm: string;
      keyDerivation: string;
    };
  }> {
    return {
      rateLimit: {
        enabled: true,
        windowMs: 15 * 60 * 1000, // 15 minutes
        maxRequests: 5 // 5 login attempts per window
      },
      authentication: {
        tokenExpiration: 15 * 60, // 15 minutes
        refreshExpiration: 7 * 24 * 60 * 60, // 7 days
        passwordPolicy: {
          minLength: 8,
          requireUppercase: true,
          requireLowercase: true,
          requireNumbers: true,
          requireSpecialChars: true
        }
      },
      encryption: {
        algorithm: 'bcrypt',
        keyDerivation: 'PBKDF2'
      }
    };
  }

  // System Health Check
  async performSystemHealthCheck(): Promise<{
    overall: 'healthy' | 'warning' | 'critical';
    services: {
      database: 'healthy' | 'warning' | 'critical';
      email: 'healthy' | 'warning' | 'critical';
      sms: 'healthy' | 'warning' | 'critical';
      webhooks: 'healthy' | 'warning' | 'critical';
    };
    metrics: {
      uptime: number;
      memoryUsage: NodeJS.MemoryUsage;
      activeConnections: number;
    };
  }> {
    const services = {
      database: await this.checkDatabaseConnection() ? 'healthy' as const : 'critical' as const,
      email: await this.checkSmtpAvailability() ? 'healthy' as const : 'warning' as const,
      sms: await this.checkSmsAvailability() ? 'healthy' as const : 'warning' as const,
      webhooks: 'healthy' as const
    };

    const criticalServices = Object.values(services).filter(status => status === 'critical').length;
    const warningServices = Object.values(services).filter(status => status === 'warning').length;

    let overall: 'healthy' | 'warning' | 'critical' = 'healthy';
    if (criticalServices > 0) {
      overall = 'critical';
    } else if (warningServices > 0) {
      overall = 'warning';
    }

    return {
      overall,
      services,
      metrics: {
        uptime: process.uptime(),
        memoryUsage: process.memoryUsage(),
        activeConnections: 0 // Could be implemented with actual connection tracking
      }
    };
  }
}

export const configurationService = new ConfigurationService();

server/emailService.ts.content:
import nodemailer from 'nodemailer';
import { SmtpServer, Recipient, Campaign, EmailTemplate } from '@shared/schema';
import { storage } from './storage';

export interface EmailContent {
  subject: string;
  html: string;
  text?: string;
  attachments?: Array<{
    filename: string;
    content: Buffer;
    cid?: string;
  }>;
}

export interface EmailSendResult {
  success: boolean;
  messageId?: string;
  error?: string;
  recipient: string;
}

export class EmailService {
  private transporter: nodemailer.Transporter | null = null;

  async initializeTransporter(smtpServer: SmtpServer): Promise<void> {
    this.transporter = nodemailer.createTransport({
      host: smtpServer.host,
      port: smtpServer.port,
      secure: smtpServer.secure,
      auth: {
        user: smtpServer.username,
        pass: smtpServer.password,
      },
      pool: true,
      maxConnections: 5,
      maxMessages: 100,
    } as any);

    // Verify the connection
    try {
      if (this.transporter) {
        await this.transporter.verify();
        console.log('SMTP connection verified successfully');
      }
    } catch (error) {
      console.error('SMTP verification failed:', error);
      throw new Error('Failed to verify SMTP connection');
    }
  }

  async sendEmail(
    to: string,
    from: string,
    content: EmailContent,
    trackingData?: {
      campaignId: string;
      recipientId: string;
      domain: string;
    }
  ): Promise<EmailSendResult> {
    if (!this.transporter) {
      return {
        success: false,
        error: 'Email transporter not initialized',
        recipient: to,
      };
    }

    try {
      // Add tracking pixels and links if tracking data provided
      let htmlContent = content.html;
      if (trackingData) {
        htmlContent = this.addEmailTracking(content.html, trackingData);
      }

      const mailOptions: nodemailer.SendMailOptions = {
        from,
        to,
        subject: content.subject,
        html: htmlContent,
        text: content.text,
        attachments: content.attachments,
      };

      const result = await this.transporter.sendMail(mailOptions);

      return {
        success: true,
        messageId: result.messageId,
        recipient: to,
      };
    } catch (error) {
      console.error('Failed to send email:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        recipient: to,
      };
    }
  }

  private addEmailTracking(html: string, trackingData: {
    campaignId: string;
    recipientId: string;
    domain: string;
  }): string {
    const { campaignId, recipientId, domain } = trackingData;

    // Add tracking pixel for open tracking
    const trackingPixel = `<img src="https://${domain}/track/open/${campaignId}/${recipientId}" width="1" height="1" style="display:none;" />`;

    // Replace all links with tracking links
    const trackedHtml = html.replace(
      /href=["']([^"']+)["']/g,
      `href="https://${domain}/track/click/${campaignId}/${recipientId}?url=$1"`
    );

    // Add tracking pixel before closing body tag
    return trackedHtml.replace('</body>', `${trackingPixel}</body>`);
  }

  async testSmtpConnection(smtpConfig: any): Promise<boolean> {
    try {
      const transporter = this.createTransporter(smtpConfig);
      await transporter.verify();
      return true;
    } catch (error) {
      console.error('SMTP connection test failed:', error);
      return false;
    }
  }

  async testSmtpAuthentication(smtpConfig: any): Promise<boolean> {
    try {
      const transporter = this.createTransporter(smtpConfig);
      await transporter.verify();
      return true;
    } catch (error) {
      console.error('SMTP authentication test failed:', error);
      return false;
    }
  }

  private createTransporter(smtpConfig: any): nodemailer.Transporter {
    return nodemailer.createTransport({
      host: smtpConfig.host,
      port: smtpConfig.port,
      secure: smtpConfig.secure,
      auth: {
        user: smtpConfig.username,
        pass: smtpConfig.password,
      },
    });
  }

  async sendTestEmail(smtpServer: SmtpServer, testEmail: string): Promise<EmailSendResult> {
    await this.initializeTransporter(smtpServer);

    const testContent: EmailContent = {
      subject: 'zSPAM - SMTP Test Email',
      html: `
        <h2>SMTP Configuration Test</h2>
        <p>If you receive this email, your SMTP server configuration is working correctly.</p>
        <p><strong>Server:</strong> ${smtpServer.host}:${smtpServer.port}</p>
        <p><strong>Time:</strong> ${new Date().toISOString()}</p>
      `,
      text: 'SMTP configuration test successful',
    };

    return await this.sendEmail(testEmail, smtpServer.username, testContent);
  }

  async personalizeEmailContent(
    template: EmailTemplate,
    recipient: Recipient,
    campaign: Campaign
  ): Promise<EmailContent> {
    let subject = template.subject;
    let html = template.htmlContent;
    let text = template.textContent || '';

    // Basic personalization variables
    const personalizations = {
      '{{name}}': recipient.name || recipient.firstName || 'User',
      '{{first_name}}': recipient.firstName || 'User',
      '{{last_name}}': recipient.lastName || '',
      '{{email}}': recipient.email,
      '{{company}}': recipient.company || '',
      '{{position}}': recipient.position || '',
      '{{department}}': recipient.department || '',
    };

    // Apply personalizations
    for (const [placeholder, value] of Object.entries(personalizations)) {
      const regex = new RegExp(placeholder.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g');
      subject = subject.replace(regex, value);
      html = html.replace(regex, value);
      text = text.replace(regex, value);
    }

    return {
      subject,
      html,
      text,
    };
  }

  close(): void {
    if (this.transporter) {
      this.transporter.close();
      this.transporter = null;
    }
  }
}

export const emailService = new EmailService();

server/environmentService.ts.content:
export interface EnvironmentConfiguration {
  name: 'development' | 'staging' | 'production';

  // Application settings
  application: {
    host: string;
    port: number;
    baseUrl: string;
    logLevel: 'debug' | 'info' | 'warn' | 'error';
    enableMetrics: boolean;
    enableProfiling: boolean;
    corsOrigins: string[];
    trustProxy: boolean;
  };

  // Database configuration
  database: {
    host: string;
    port: number;
    name: string;
    username: string;
    password: string;
    ssl: boolean;
    connectionPoolSize: number;
    queryTimeout: number;
    backupEnabled: boolean;
    backupSchedule: string;
  };

  // Security settings
  security: {
    sessionSecret: string;
    jwtSecret: string;
    encryptionKey: string;
    hashSaltRounds: number;
    rateLimiting: {
      enabled: boolean;
      maxRequestsPerMinute: number;
      maxRequestsPerHour: number;
    };
    ipWhitelist: string[];
    securityHeaders: boolean;
    contentSecurityPolicy: boolean;
  };

  // Email configuration
  email: {
    defaultProvider: string;
    providers: {
      [key: string]: {
        host: string;
        port: number;
        secure: boolean;
        username: string;
        password: string;
        maxConcurrentConnections: number;
        rateLimitPerHour: number;
      };
    };
  };

  // External integrations
  integrations: {
    ldap: {
      enabled: boolean;
      primaryServer: string;
      backupServers: string[];
      connectionTimeout: number;
    };
    siem: {
      enabled: boolean;
      bufferSize: number;
      flushInterval: number;
      retryAttempts: number;
    };
    monitoring: {
      enabled: boolean;
      metricsInterval: number;
      alertingEnabled: boolean;
      healthCheckInterval: number;
    };
  };

  // Performance settings
  performance: {
    caching: {
      enabled: boolean;
      ttl: number;
      maxSize: number;
    };
    compression: {
      enabled: boolean;
      level: number;
    };
    clustering: {
      enabled: boolean;
      workers: number;
    };
  };

  // Compliance and audit
  compliance: {
    dataRetentionDays: number;
    auditLogging: boolean;
    gdprCompliance: boolean;
    hipaaCompliance: boolean;
    anonymizeData: boolean;
  };
}

export class EnvironmentService {
  private currentConfig: EnvironmentConfiguration;

  constructor() {
    this.currentConfig = this.loadConfiguration();
    this.validateConfiguration();
  }

  private loadConfiguration(): EnvironmentConfiguration {
    const env = (process.env.NODE_ENV || 'development') as 'development' | 'staging' | 'production';

    const baseConfig: EnvironmentConfiguration = {
      name: env,
      application: {
        host: process.env.HOST || '0.0.0.0',
        port: parseInt(process.env.PORT || '5000'),
        baseUrl: process.env.BASE_URL || 'http://localhost:5000',
        logLevel: (process.env.LOG_LEVEL as any) || 'info',
        enableMetrics: process.env.ENABLE_METRICS === 'true',
        enableProfiling: process.env.ENABLE_PROFILING === 'true',
        corsOrigins: process.env.CORS_ORIGINS?.split(',') || ['*'],
        trustProxy: process.env.TRUST_PROXY === 'true'
      },
      database: {
        host: process.env.DB_HOST || 'localhost',
        port: parseInt(process.env.DB_PORT || '5432'),
        name: process.env.DB_NAME || 'zspam',
        username: process.env.DB_USERNAME || 'postgres',
        password: process.env.DB_PASSWORD || '',
        ssl: process.env.DB_SSL === 'true',
        connectionPoolSize: parseInt(process.env.DB_POOL_SIZE || '10'),
        queryTimeout: parseInt(process.env.DB_QUERY_TIMEOUT || '30000'),
        backupEnabled: process.env.DB_BACKUP_ENABLED === 'true',
        backupSchedule: process.env.DB_BACKUP_SCHEDULE || '0 2 * * *'
      },
      security: {
        sessionSecret: process.env.SESSION_SECRET || 'dev-session-secret',
        jwtSecret: process.env.JWT_SECRET || 'dev-jwt-secret',
        encryptionKey: process.env.ENCRYPTION_KEY || 'dev-encryption-key',
        hashSaltRounds: parseInt(process.env.HASH_SALT_ROUNDS || '12'),
        rateLimiting: {
          enabled: process.env.RATE_LIMITING_ENABLED !== 'false',
          maxRequestsPerMinute: parseInt(process.env.RATE_LIMIT_PER_MINUTE || '100'),
          maxRequestsPerHour: parseInt(process.env.RATE_LIMIT_PER_HOUR || '1000')
        },
        ipWhitelist: process.env.IP_WHITELIST?.split(',') || [],
        securityHeaders: process.env.SECURITY_HEADERS !== 'false',
        contentSecurityPolicy: process.env.CSP_ENABLED === 'true'
      },
      email: {
        defaultProvider: process.env.DEFAULT_EMAIL_PROVIDER || 'primary',
        providers: {
          primary: {
            host: process.env.SMTP_HOST || 'localhost',
            port: parseInt(process.env.SMTP_PORT || '587'),
            secure: process.env.SMTP_SECURE === 'true',
            username: process.env.SMTP_USERNAME || '',
            password: process.env.SMTP_PASSWORD || '',
            maxConcurrentConnections: parseInt(process.env.SMTP_MAX_CONNECTIONS || '5'),
            rateLimitPerHour: parseInt(process.env.SMTP_RATE_LIMIT || '100')
          }
        }
      },
      integrations: {
        ldap: {
          enabled: process.env.LDAP_ENABLED === 'true',
          primaryServer: process.env.LDAP_PRIMARY_SERVER || '',
          backupServers: process.env.LDAP_BACKUP_SERVERS?.split(',') || [],
          connectionTimeout: parseInt(process.env.LDAP_TIMEOUT || '5000')
        },
        siem: {
          enabled: process.env.SIEM_ENABLED === 'true',
          bufferSize: parseInt(process.env.SIEM_BUFFER_SIZE || '100'),
          flushInterval: parseInt(process.env.SIEM_FLUSH_INTERVAL || '300'),
          retryAttempts: parseInt(process.env.SIEM_RETRY_ATTEMPTS || '3')
        },
        monitoring: {
          enabled: process.env.MONITORING_ENABLED !== 'false',
          metricsInterval: parseInt(process.env.METRICS_INTERVAL || '60'),
          alertingEnabled: process.env.ALERTING_ENABLED !== 'false',
          healthCheckInterval: parseInt(process.env.HEALTH_CHECK_INTERVAL || '30')
        }
      },
      performance: {
        caching: {
          enabled: process.env.CACHING_ENABLED !== 'false',
          ttl: parseInt(process.env.CACHE_TTL || '3600'),
          maxSize: parseInt(process.env.CACHE_MAX_SIZE || '100')
        },
        compression: {
          enabled: process.env.COMPRESSION_ENABLED !== 'false',
          level: parseInt(process.env.COMPRESSION_LEVEL || '6')
        },
        clustering: {
          enabled: process.env.CLUSTERING_ENABLED === 'true',
          workers: parseInt(process.env.CLUSTER_WORKERS || '0')
        }
      },
      compliance: {
        dataRetentionDays: parseInt(process.env.DATA_RETENTION_DAYS || '365'),
        auditLogging: process.env.AUDIT_LOGGING !== 'false',
        gdprCompliance: process.env.GDPR_COMPLIANCE === 'true',
        hipaaCompliance: process.env.HIPAA_COMPLIANCE === 'true',
        anonymizeData: process.env.ANONYMIZE_DATA === 'true'
      }
    };

    // Environment-specific overrides
    return this.applyEnvironmentOverrides(baseConfig, env);
  }

  private applyEnvironmentOverrides(config: EnvironmentConfiguration, env: string): EnvironmentConfiguration {
    switch (env) {
      case 'development':
        return {
          ...config,
          application: {
            ...config.application,
            logLevel: 'debug',
            enableMetrics: true,
            enableProfiling: true
          },
          security: {
            ...config.security,
            hashSaltRounds: 10, // Faster for development
            rateLimiting: {
              ...config.security.rateLimiting,
              enabled: false
            }
          },
          performance: {
            ...config.performance,
            clustering: {
              ...config.performance.clustering,
              enabled: false
            }
          }
        };

      case 'staging':
        return {
          ...config,
          application: {
            ...config.application,
            logLevel: 'info',
            enableMetrics: true,
            enableProfiling: false
          },
          security: {
            ...config.security,
            rateLimiting: {
              ...config.security.rateLimiting,
              enabled: true,
              maxRequestsPerMinute: 200,
              maxRequestsPerHour: 2000
            }
          },
          database: {
            ...config.database,
            backupEnabled: true,
            connectionPoolSize: 20
          }
        };

      case 'production':
        return {
          ...config,
          application: {
            ...config.application,
            logLevel: 'warn',
            enableMetrics: true,
            enableProfiling: false,
            trustProxy: true
          },
          security: {
            ...config.security,
            hashSaltRounds: 14, // More secure for production
            rateLimiting: {
              ...config.security.rateLimiting,
              enabled: true
            },
            securityHeaders: true,
            contentSecurityPolicy: true
          },
          database: {
            ...config.database,
            ssl: true,
            backupEnabled: true,
            connectionPoolSize: 50
          },
          performance: {
            ...config.performance,
            clustering: {
              enabled: true,
              workers: 0 // Auto-detect based on CPU cores
            }
          },
          compliance: {
            ...config.compliance,
            auditLogging: true,
            gdprCompliance: true
          }
        };

      default:
        return config;
    }
  }

  private validateConfiguration(): void {
    const errors: string[] = [];

    // Validate required secrets in production
    if (this.currentConfig.name === 'production') {
      if (!process.env.SESSION_SECRET || process.env.SESSION_SECRET === 'dev-session-secret') {
        errors.push('SESSION_SECRET must be set to a secure value in production');
      }
      if (!process.env.JWT_SECRET || process.env.JWT_SECRET === 'dev-jwt-secret') {
        errors.push('JWT_SECRET must be set to a secure value in production');
      }
      if (!process.env.ENCRYPTION_KEY || process.env.ENCRYPTION_KEY === 'dev-encryption-key') {
        errors.push('ENCRYPTION_KEY must be set to a secure value in production');
      }
    }

    // Validate database configuration
    if (this.currentConfig.name !== 'development' && !this.currentConfig.database.password) {
      errors.push('Database password must be set for non-development environments');
    }

    // Validate email configuration if enabled
    if (this.currentConfig.email.providers.primary.host !== 'localhost' && 
        !this.currentConfig.email.providers.primary.username) {
      errors.push('SMTP username must be set for external email providers');
    }

    if (errors.length > 0) {
      console.error('Configuration validation errors:');
      errors.forEach(error => console.error(`  - ${error}`));

      if (this.currentConfig.name === 'production') {
        throw new Error('Invalid production configuration. Please fix the errors above.');
      } else {
        console.warn('Configuration warnings detected. Application will continue with default values.');
      }
    }
  }

  getConfiguration(): EnvironmentConfiguration {
    return this.currentConfig;
  }

  getEnvironment(): string {
    return this.currentConfig.name;
  }

  isDevelopment(): boolean {
    return this.currentConfig.name === 'development';
  }

  isStaging(): boolean {
    return this.currentConfig.name === 'staging';
  }

  isProduction(): boolean {
    return this.currentConfig.name === 'production';
  }

  // Configuration utilities
  getDatabaseUrl(): string {
    const db = this.currentConfig.database;
    const protocol = db.ssl ? 'postgresql' : 'postgres';
    return `${protocol}://${db.username}:${db.password}@${db.host}:${db.port}/${db.name}${db.ssl ? '?sslmode=require' : ''}`;
  }

  getLogLevel(): string {
    return this.currentConfig.application.logLevel;
  }

  getSecurityConfig(): any {
    return this.currentConfig.security;
  }

  getPerformanceConfig(): any {
    return this.currentConfig.performance;
  }

  getComplianceConfig(): any {
    return this.currentConfig.compliance;
  }

  // Environment-specific feature flags
  isFeatureEnabled(feature: string): boolean {
    const flags: { [key: string]: boolean } = {
      'metrics': this.currentConfig.application.enableMetrics,
      'profiling': this.currentConfig.application.enableProfiling,
      'clustering': this.currentConfig.performance.clustering.enabled,
      'caching': this.currentConfig.performance.caching.enabled,
      'compression': this.currentConfig.performance.compression.enabled,
      'rate-limiting': this.currentConfig.security.rateLimiting.enabled,
      'audit-logging': this.currentConfig.compliance.auditLogging,
      'gdpr-compliance': this.currentConfig.compliance.gdprCompliance,
      'ldap-integration': this.currentConfig.integrations.ldap.enabled,
      'siem-integration': this.currentConfig.integrations.siem.enabled,
      'monitoring': this.currentConfig.integrations.monitoring.enabled
    };

    return flags[feature] || false;
  }

  // Configuration validation for deployment
  validateForDeployment(): { valid: boolean; errors: string[]; warnings: string[] } {
    const errors: string[] = [];
    const warnings: string[] = [];

    // Security validations
    if (this.isProduction()) {
      if (this.currentConfig.security.sessionSecret.length < 32) {
        errors.push('Session secret must be at least 32 characters in production');
      }

      if (this.currentConfig.security.jwtSecret.length < 32) {
        errors.push('JWT secret must be at least 32 characters in production');
      }

      if (!this.currentConfig.database.ssl) {
        errors.push('Database SSL must be enabled in production');
      }

      if (this.currentConfig.application.corsOrigins.includes('*')) {
        warnings.push('CORS is configured to allow all origins in production');
      }
    }

    // Performance validations
    if (this.currentConfig.database.connectionPoolSize > 100) {
      warnings.push('Database connection pool size is very high (>100)');
    }

    if (this.currentConfig.performance.compression.enabled && this.currentConfig.performance.compression.level > 8) {
      warnings.push('High compression level may impact performance');
    }

    // Integration validations
    if (this.currentConfig.integrations.ldap.enabled && !this.currentConfig.integrations.ldap.primaryServer) {
      errors.push('LDAP primary server must be configured when LDAP is enabled');
    }

    return {
      valid: errors.length === 0,
      errors,
      warnings
    };
  }

  // Generate deployment checklist
  generateDeploymentChecklist(): string[] {
    const checklist: string[] = [];

    checklist.push('âœ“ Environment variables configured');
    checklist.push('âœ“ Database connection tested');
    checklist.push('âœ“ Security settings validated');

    if (this.currentConfig.database.backupEnabled) {
      checklist.push('âœ“ Database backup schedule configured');
    }

    if (this.currentConfig.integrations.ldap.enabled) {
      checklist.push('âœ“ LDAP integration tested');
    }

    if (this.currentConfig.integrations.siem.enabled) {
      checklist.push('âœ“ SIEM integration configured');
    }

    if (this.currentConfig.integrations.monitoring.enabled) {
      checklist.push('âœ“ Monitoring and alerting configured');
    }

    if (this.isProduction()) {
      checklist.push('âœ“ Production secrets configured');
      checklist.push('âœ“ SSL certificates installed');
      checklist.push('âœ“ Load balancer configured');
      checklist.push('âœ“ CDN configured (if applicable)');
      checklist.push('âœ“ Monitoring dashboards set up');
      checklist.push('âœ“ Backup and recovery tested');
    }

    return checklist;
  }

  // Environment migration utilities
  exportConfiguration(): string {
    const exportData = {
      environment: this.currentConfig.name,
      configuration: this.currentConfig,
      exportedAt: new Date().toISOString(),
      version: '1.0'
    };

    return JSON.stringify(exportData, null, 2);
  }

  importConfiguration(configData: string): boolean {
    try {
      const data = JSON.parse(configData);

      // Validate imported configuration
      if (!data.configuration || !data.environment) {
        throw new Error('Invalid configuration format');
      }

      console.log(`Importing configuration for ${data.environment} environment`);
      // In a real implementation, would merge and validate the configuration
      return true;
    } catch (error) {
      console.error('Failed to import configuration:', error);
      return false;
    }
  }

  // Configuration comparison
  compareEnvironments(otherEnv: string): any {
    const comparison = {
      environment: {
        current: this.currentConfig.name,
        comparing: otherEnv
      },
      differences: {
        security: {},
        performance: {},
        integrations: {}
      }
    };

    // Implementation would compare configurations between environments
    console.log(`Comparing ${this.currentConfig.name} with ${otherEnv} environment`);
    return comparison;
  }
}

