}

export interface MobileTemplate {
  id: string;
  name: string;
  category: 'banking_app' | 'delivery_sms' | 'social_media' | 'tax_refund' | 'security_alert' | 'app_update';
  platform: 'ios' | 'android' | 'cross_platform';

