    mobileHtml: string;
    cssFramework: 'bootstrap' | 'tailwind' | 'custom';
    formFields: MobileFormField[];
    credentialCapture: boolean;
    mfaCapture: boolean;
  };

  // App simulation
  appSimulation: {
    appName: string;
    iconUrl: string;
    packageName: string;
    minimumOSVersion: string;
    permissions: string[];
    screenshots: string[];
  };
}

export interface MobileFormField {
  id: string;
  type: 'text' | 'email' | 'password' | 'tel' | 'number' | 'date' | 'select' | 'file';
  label: string;
  placeholder: string;
  required: boolean;
  mobileKeyboard: 'default' | 'numeric' | 'email' | 'phone' | 'url';
  autoComplete: string;
  sensitive: boolean; // PII/credentials
}

export interface MobileSessionData {
  sessionId: string;
  deviceInfo: {
    userAgent: string;
    screenResolution: string;
    devicePixelRatio: number;
    touchSupport: boolean;
    platform: 'ios' | 'android' | 'other';
    browser: string;
    isEmulator: boolean;
  };
