    latitude: number;
    longitude: number;
    accuracy: number;
    timestamp: Date;
  };
  behaviorMetrics: {
    touchEvents: number;
    scrollEvents: number;
    orientationChanges: number;
    averageInputSpeed: number;
    formCompletionTime: number;
  };
  appInteraction?: {
    deepLinkClicked: boolean;
    appStoreRedirect: boolean;
    installAttempted: boolean;
    permissionsRequested: string[];
