  // Alert conditions
  conditions: {
    metric: 'open_rate' | 'click_rate' | 'conversion_rate' | 'bot_score' | 'failed_emails' | 'session_count' | 'risk_score';
    operator: 'greater_than' | 'less_than' | 'equals' | 'not_equals' | 'percentage_change';
    threshold: number;
    timeWindow: number; // minutes
    campaignSpecific?: boolean;
    campaignId?: string;
  };

  // Alert frequency
  frequency: {
    maxAlertsPerHour: number;
    suppressDuplicates: boolean;
    suppressionWindow: number; // minutes
    escalationThreshold: number; // trigger count before escalation
  };

  // Notification channels
  notifications: {
    email: {
      enabled: boolean;
      recipients: string[];
      subject?: string;
      includeDetails: boolean;
    };
    webhook: {
      enabled: boolean;
      url?: string;
      secret?: string;
    };
    slack: {
      enabled: boolean;
      channel?: string;
      mentionUsers?: string[];
    };
    sms: {
      enabled: boolean;
      numbers?: string[];
    };
  };

  // Auto-response actions
  autoResponse: {
    enabled: boolean;
    actions: ('pause_campaign' | 'send_notification' | 'create_incident' | 'block_ips' | 'quarantine_emails')[];
  };
}

export interface Alert {
  id: string;
  configId: string;
  timestamp: Date;
  severity: 'low' | 'medium' | 'high' | 'critical';
  status: 'new' | 'acknowledged' | 'resolved' | 'suppressed';

  // Alert details
  metric: string;
  currentValue: number;
  threshold: number;
  campaignId?: string;
  campaignName?: string;

  // Context
  context: {
    description: string;
    affectedEntities: string[];
    recommendations: string[];
    additionalData?: any;
  };

  // Tracking
  acknowledgedBy?: string;
  acknowledgedAt?: Date;
  resolvedBy?: string;
  resolvedAt?: Date;
  escalationLevel: number;
}

export interface SystemHealthMetrics {
  timestamp: Date;

  // Application metrics
  application: {
    uptime: number;
    memoryUsage: number;
    cpuUsage: number;
    activeConnections: number;
    requestsPerMinute: number;
    errorRate: number;
    responseTime: number;
  };

  // Database metrics
  database: {
    connectionCount: number;
    queryLatency: number;
    slowQueries: number;
    lockWaits: number;
    tableSizes: { [table: string]: number };
  };

  // Campaign metrics
  campaigns: {
    activeCampaigns: number;
    emailsInQueue: number;
    deliveryRate: number;
    bounceRate: number;
    avgResponseTime: number;
  };

  // Security metrics
  security: {
    suspiciousSessions: number;
    botDetections: number;
    riskScoreAverage: number;
    blockedIPs: number;
  };
}

export interface MonitoringDashboard {
  overview: {
    systemStatus: 'healthy' | 'warning' | 'critical';
    activeAlerts: number;
    uptime: string;
    lastUpdate: Date;
  };

  realTimeMetrics: {
    emailsSentLastHour: number;
    activeSessionsCount: number;
    currentThroughput: number;
    errorRateLastHour: number;
  };

  trendData: {
    hourlyActivity: { [hour: string]: number };
    dailyMetrics: { [date: string]: any };
    weeklyTrends: { [week: string]: any };
  };

  topAlerts: Alert[];
  systemHealth: SystemHealthMetrics;
}

export class MonitoringService {
  private alerts: Map<string, Alert> = new Map();
  private alertConfigs: Map<string, AlertConfiguration> = new Map();
  private systemMetrics: SystemHealthMetrics[] = [];
  private suppressedAlerts: Set<string> = new Set();

  constructor() {
    this.initializeDefaultAlerts();
    this.startMonitoring();
  }

  private initializeDefaultAlerts(): void {
    const defaultAlerts: AlertConfiguration[] = [
      {
        id: 'high-conversion-rate',
        name: 'High Conversion Rate Alert',
        description: 'Triggers when conversion rate exceeds threshold',
        enabled: true,
        conditions: {
          metric: 'conversion_rate',
          operator: 'greater_than',
          threshold: 15,
          timeWindow: 60,
          campaignSpecific: false
        },
        frequency: {
          maxAlertsPerHour: 2,
          suppressDuplicates: true,
          suppressionWindow: 30,
          escalationThreshold: 3
        },
        notifications: {
          email: {
            enabled: true,
            recipients: ['security@company.com', 'admin@company.com'],
            subject: 'Security Alert: High Phishing Susceptibility Detected',
            includeDetails: true
          },
          webhook: { enabled: false },
          slack: { enabled: false },
          sms: { enabled: false }
        },
        autoResponse: {
