                <button type="submit" class="mobile-submit-btn">
                    <span>ðŸ”</span>
                    <span>Verify & Secure Account</span>
                </button>
            </form>

            <div class="biometric-prompt">
                <div class="biometric-icon">ðŸ‘¤</div>
